class SimpleAIDecisionTree:
    def __init__(self):
        self.temperature = 0
        self.humidity = 0

    def set_conditions(self, temperature, humidity):
        self.temperature = temperature
        self.humidity = humidity

    def make_decision(self):
        # Simple AI decision making based on conditions
        if self.temperature > 25:
            if self.humidity > 60:
                return "Turn on air conditioning"
            else:
                return "Open windows for ventilation"
        elif self.temperature < 15:
            return "Turn on heating"
        else:
            return "Maintain current temperature"

# Example usage
ai_system = SimpleAIDecisionTree()
ai_system.set_conditions(28, 70)
decision = ai_system.make_decision()
print(f"AI Decision: {decision}")

# AI: Broad field of intelligent systems
class ArtificialIntelligence:
    def __init__(self):
        self.name = "AI System"

    def solve_problem(self, problem):
        return f"AI solving: {problem}"

# ML: Subset of AI that learns from data
class MachineLearning(ArtificialIntelligence):
    def __init__(self):
        super().__init__()
        self.name = "ML System"
        self.training_data = []

    def train(self, data):
        self.training_data = data
        return f"Trained on {len(data)} samples"

    def predict(self, input_data):
        # Simple prediction based on training data
        if self.training_data:
            return f"ML prediction for: {input_data}"
        return "No training data available"

# DL: Subset of ML using neural networks
class DeepLearning(MachineLearning):
    def __init__(self):
        super().__init__()
        self.name = "DL System"
        self.layers = []

    def add_layer(self, layer_size):
        self.layers.append(layer_size)
        return f"Added layer with {layer_size} neurons"

    def forward_pass(self, input_data):
        # Simulate neural network forward pass
        result = input_data
        for layer_size in self.layers:
            result = f"Processed through {layer_size}-neuron layer"
        return f"DL result: {result}"

# Example usage
dl_system = DeepLearning()
dl_system.train([1, 2, 3, 4, 5])
dl_system.add_layer(10)
dl_system.add_layer(5)
print(dl_system.solve_problem("image classification"))
print(dl_system.predict("new image"))
print(dl_system.forward_pass("input data"))
