import java.util.*;

public class ListDemo {
    public static void main(String[] args) {
        // ArrayList - dynamic array
        List<String> arrayList = new ArrayList<>();
        arrayList.add("Apple");
        arrayList.add("Banana");
        arrayList.add("Orange");
        arrayList.add(1, "Mango"); // Insert at index 1

        System.out.println("ArrayList: " + arrayList);
        System.out.println("Size: " + arrayList.size());
        System.out.println("Contains 'Apple': " + arrayList.contains("Apple"));

        // LinkedList - doubly linked list
        List<Integer> linkedList = new LinkedList<>();
        linkedList.add(10);
        linkedList.add(20);
        linkedList.add(30);
        linkedList.addFirst(5);
        linkedList.addLast(40);

        System.out.println("\nLinkedList: " + linkedList);

        // Vector - thread-safe dynamic array
        List<Double> vector = new Vector<>();
        vector.add(1.5);
        vector.add(2.5);
        vector.add(3.5);

        System.out.println("\nVector: " + vector);

        // Common List operations
        List<String> fruits = new ArrayList<>();
        fruits.addAll(Arrays.asList("Apple", "Banana", "Orange", "Grape"));

        // Iterating through List
        System.out.println("\n=== Iterating through List ===");
        for (int i = 0; i < fruits.size(); i++) {
            System.out.println("Index " + i + ": " + fruits.get(i));
        }

        // Enhanced for loop
        System.out.println("\nEnhanced for loop:");
        for (String fruit : fruits) {
            System.out.println(fruit);
        }

        // Iterator
        System.out.println("\nIterator:");
        Iterator<String> iterator = fruits.iterator();
        while (iterator.hasNext()) {
            System.out.println(iterator.next());
        }

        // ListIterator (bidirectional)
        System.out.println("\nListIterator (forward):");
        ListIterator<String> listIterator = fruits.listIterator();
        while (listIterator.hasNext()) {
            System.out.println(listIterator.next());
        }

        System.out.println("\nListIterator (backward):");
        while (listIterator.hasPrevious()) {
            System.out.println(listIterator.previous());
        }

        // List operations
        System.out.println("\n=== List Operations ===");
        fruits.set(1, "Blueberry");
        System.out.println("After set: " + fruits);

        fruits.remove(2);
        System.out.println("After remove: " + fruits);

        fruits.remove("Apple");
        System.out.println("After remove object: " + fruits);

        // Sublist
        List<String> subList = fruits.subList(0, 2);
        System.out.println("SubList: " + subList);

        // Sorting
        Collections.sort(fruits);
        System.out.println("After sorting: " + fruits);

        // Custom sorting
        fruits.sort((a, b) -> b.compareTo(a)); // Reverse order
        System.out.println("After reverse sorting: " + fruits);
    }
}

import java.util.*;

public class SetDemo {
    public static void main(String[] args) {
        // HashSet - unordered, no duplicates
        Set<String> hashSet = new HashSet<>();
        hashSet.add("Apple");
        hashSet.add("Banana");
        hashSet.add("Apple"); // Duplicate, won't be added
        hashSet.add("Orange");
        hashSet.add("Grape");

        System.out.println("HashSet: " + hashSet);
        System.out.println("Size: " + hashSet.size());

        // LinkedHashSet - maintains insertion order
        Set<String> linkedHashSet = new LinkedHashSet<>();
        linkedHashSet.add("First");
        linkedHashSet.add("Second");
        linkedHashSet.add("Third");
        linkedHashSet.add("First"); // Duplicate

        System.out.println("\nLinkedHashSet: " + linkedHashSet);

        // TreeSet - sorted order
        Set<Integer> treeSet = new TreeSet<>();
        treeSet.add(5);
        treeSet.add(2);
        treeSet.add(8);
        treeSet.add(1);
        treeSet.add(3);

        System.out.println("\nTreeSet: " + treeSet);

        // Custom objects in Set
        Set<Student> studentSet = new HashSet<>();
        studentSet.add(new Student("Alice", 101));
        studentSet.add(new Student("Bob", 102));
        studentSet.add(new Student("Alice", 101)); // Duplicate

        System.out.println("\nStudent Set: " + studentSet);

        // Set operations
        Set<String> set1 = new HashSet<>(Arrays.asList("A", "B", "C", "D"));
        Set<String> set2 = new HashSet<>(Arrays.asList("C", "D", "E", "F"));

        System.out.println("\nSet 1: " + set1);
        System.out.println("Set 2: " + set2);

        // Union
        Set<String> union = new HashSet<>(set1);
        union.addAll(set2);
        System.out.println("Union: " + union);

        // Intersection
        Set<String> intersection = new HashSet<>(set1);
        intersection.retainAll(set2);
        System.out.println("Intersection: " + intersection);

        // Difference
        Set<String> difference = new HashSet<>(set1);
        difference.removeAll(set2);
        System.out.println("Difference (set1 - set2): " + difference);

        // TreeSet with custom comparator
        Set<Person> personSet = new TreeSet<>((p1, p2) -> {
            int ageCompare = Integer.compare(p1.getAge(), p2.getAge());
            if (ageCompare != 0) return ageCompare;
            return p1.getName().compareTo(p2.getName());
        });

        personSet.add(new Person("Alice", 25));
        personSet.add(new Person("Bob", 30));
        personSet.add(new Person("Charlie", 25));
        personSet.add(new Person("David", 20));

        System.out.println("\nSorted Person Set:");
        for (Person person : personSet) {
            System.out.println(person);
        }
    }
}

// Student class for Set demonstration
class Student {
    private String name;
    private int id;

    public Student(String name, int id) {
        this.name = name;
        this.id = id;
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj) return true;
        if (obj == null || getClass() != obj.getClass()) return false;
        Student student = (Student) obj;
        return id == student.id && Objects.equals(name, student.name);
    }

    @Override
    public int hashCode() {
        return Objects.hash(name, id);
    }

    @Override
    public String toString() {
        return "Student{name='" + name + "', id=" + id + '}';
    }
}

// Person class for TreeSet demonstration
class Person {
    private String name;
    private int age;

    public Person(String name, int age) {
        this.name = name;
        this.age = age;
    }

    public String getName() { return name; }
    public int getAge() { return age; }

    @Override
    public String toString() {
        return "Person{name='" + name + "', age=" + age + '}';
    }
}

import java.util.*;

public class MapDemo {
    public static void main(String[] args) {
        // HashMap - unordered key-value pairs
        Map<String, Integer> hashMap = new HashMap<>();
        hashMap.put("Apple", 5);
        hashMap.put("Banana", 3);
        hashMap.put("Orange", 7);
        hashMap.put("Apple", 6); // Overwrites previous value

        System.out.println("HashMap: " + hashMap);
        System.out.println("Apple count: " + hashMap.get("Apple"));
        System.out.println("Contains 'Grape': " + hashMap.containsKey("Grape"));

        // LinkedHashMap - maintains insertion order
        Map<String, String> linkedHashMap = new LinkedHashMap<>();
        linkedHashMap.put("First", "Value1");
        linkedHashMap.put("Second", "Value2");
        linkedHashMap.put("Third", "Value3");

        System.out.println("\nLinkedHashMap: " + linkedHashMap);

        // TreeMap - sorted by keys
        Map<String, Double> treeMap = new TreeMap<>();
        treeMap.put("Zebra", 100.0);
        treeMap.put("Apple", 50.0);
        treeMap.put("Banana", 75.0);

        System.out.println("\nTreeMap: " + treeMap);

        // Map operations
        Map<String, Integer> scores = new HashMap<>();
        scores.put("Alice", 85);
        scores.put("Bob", 92);
        scores.put("Charlie", 78);
        scores.put("David", 95);

        System.out.println("\n=== Map Operations ===");
        System.out.println("Scores: " + scores);

        // Iterating through Map
        System.out.println("\nKey-Value pairs:");
        for (Map.Entry<String, Integer> entry : scores.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        // Iterating through keys
        System.out.println("\nKeys:");
        for (String key : scores.keySet()) {
            System.out.println(key);
        }

        // Iterating through values
        System.out.println("\nValues:");
        for (Integer value : scores.values()) {
            System.out.println(value);
        }

        // Map utility methods
        System.out.println("\n=== Map Utilities ===");
        scores.putIfAbsent("Alice", 90); // Won't overwrite
        scores.putIfAbsent("Eve", 88); // Will add
        System.out.println("After putIfAbsent: " + scores);

        // Compute methods
        scores.compute("Alice", (key, value) -> value + 5);
        System.out.println("After compute: " + scores);

        scores.computeIfPresent("Bob", (key, value) -> value * 2);
        scores.computeIfAbsent("Frank", key -> 70);
        System.out.println("After computeIfPresent/Absent: " + scores);

        // Merge method
        scores.merge("Alice", 10, (oldValue, newValue) -> oldValue + newValue);
        System.out.println("After merge: " + scores);

        // Remove method
        scores.remove("Charlie");
        System.out.println("After remove: " + scores);

        // Custom object as key
        Map<Student, String> studentGrades = new HashMap<>();
        Student alice = new Student("Alice", 101);
        Student bob = new Student("Bob", 102);

        studentGrades.put(alice, "A");
        studentGrades.put(bob, "B");

        System.out.println("\nStudent Grades: " + studentGrades);

        // Nested Map
        Map<String, Map<String, Integer>> nestedMap = new HashMap<>();

        Map<String, Integer> mathScores = new HashMap<>();
        mathScores.put("Alice", 85);
        mathScores.put("Bob", 92);

        Map<String, Integer> scienceScores = new HashMap<>();
        scienceScores.put("Alice", 90);
        scienceScores.put("Bob", 88);

        nestedMap.put("Math", mathScores);
        nestedMap.put("Science", scienceScores);

        System.out.println("\nNested Map: " + nestedMap);

        // Accessing nested values
        System.out.println("Alice's Math score: " + nestedMap.get("Math").get("Alice"));
    }
}

