import java.util.*;

public class ListDemo {
    public static void main(String[] args) {
        // ArrayList
        List<String> arrayList = new ArrayList<>();
        arrayList.add("Apple");
        arrayList.add("Banana");
        arrayList.add(1, "Mango");

        System.out.println("ArrayList: " + arrayList);

        // LinkedList
        List<Integer> linkedList = new LinkedList<>();
        linkedList.add(10);
        linkedList.add(20);
        linkedList.addFirst(5);

        System.out.println("LinkedList: " + linkedList);

        // Iterating
        for (String item : arrayList) {
            System.out.println(item);
        }

        // Basic operations
        arrayList.set(1, "Orange");
        arrayList.remove(2);
        System.out.println("After operations: " + arrayList);
    }
}

import java.util.*;

public class SetDemo {
    public static void main(String[] args) {
        // HashSet - no duplicates
        Set<String> hashSet = new HashSet<>();
        hashSet.add("Apple");
        hashSet.add("Banana");
        hashSet.add("Apple"); // Duplicate, won't be added

        System.out.println("HashSet: " + hashSet);

        // TreeSet - sorted order
        Set<Integer> treeSet = new TreeSet<>();
        treeSet.add(5);
        treeSet.add(2);
        treeSet.add(8);

        System.out.println("TreeSet: " + treeSet);

        // Set operations
        Set<String> set1 = new HashSet<>(Arrays.asList("A", "B", "C"));
        Set<String> set2 = new HashSet<>(Arrays.asList("C", "D", "E"));

        // Union
        Set<String> union = new HashSet<>(set1);
        union.addAll(set2);
        System.out.println("Union: " + union);

        // Intersection
        Set<String> intersection = new HashSet<>(set1);
        intersection.retainAll(set2);
        System.out.println("Intersection: " + intersection);
    }
}

import java.util.*;

public class MapDemo {
    public static void main(String[] args) {
        // HashMap
        Map<String, Integer> hashMap = new HashMap<>();
        hashMap.put("Apple", 5);
        hashMap.put("Banana", 3);
        hashMap.put("Apple", 6); // Overwrites

        System.out.println("HashMap: " + hashMap);
        System.out.println("Apple count: " + hashMap.get("Apple"));

        // TreeMap - sorted
        Map<String, Double> treeMap = new TreeMap<>();
        treeMap.put("Zebra", 100.0);
        treeMap.put("Apple", 50.0);

        System.out.println("TreeMap: " + treeMap);

        // Iterating
        for (Map.Entry<String, Integer> entry : hashMap.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }
    }
}

import java.util.*;

public class QueueDequeDemo {
    public static void main(String[] args) {
        // PriorityQueue - elements ordered by priority
        Queue<Integer> priorityQueue = new PriorityQueue<>();
        priorityQueue.offer(5);
        priorityQueue.offer(2);
        priorityQueue.offer(8);
        priorityQueue.offer(1);
        priorityQueue.offer(3);

        System.out.println("PriorityQueue: " + priorityQueue);
        System.out.println("Peek: " + priorityQueue.peek());

        System.out.println("\nRemoving elements:");
        while (!priorityQueue.isEmpty()) {
            System.out.print(priorityQueue.poll() + " ");
        }
        System.out.println();

        // Custom comparator for PriorityQueue
        Queue<String> stringPriorityQueue = new PriorityQueue<>((a, b) -> b.compareTo(a));
        stringPriorityQueue.offer("Apple");
        stringPriorityQueue.offer("Banana");
        stringPriorityQueue.offer("Orange");

        System.out.println("\nString PriorityQueue (reverse order): " + stringPriorityQueue);

        // LinkedList as Queue
        Queue<String> linkedListQueue = new LinkedList<>();
        linkedListQueue.offer("First");
        linkedListQueue.offer("Second");
        linkedListQueue.offer("Third");

        System.out.println("\nLinkedList Queue: " + linkedListQueue);
        System.out.println("Peek: " + linkedListQueue.peek());
        System.out.println("Poll: " + linkedListQueue.poll());
        System.out.println("After poll: " + linkedListQueue);

        // Deque (Double-ended queue)
        Deque<String> deque = new LinkedList<>();

        // Adding elements
        deque.addFirst("First");
        deque.addLast("Last");
        deque.offerFirst("NewFirst");
        deque.offerLast("NewLast");

        System.out.println("\nDeque: " + deque);

        // Accessing elements
        System.out.println("First element: " + deque.getFirst());
        System.out.println("Last element: " + deque.getLast());
        System.out.println("Peek first: " + deque.peekFirst());
        System.out.println("Peek last: " + deque.peekLast());

        // Removing elements
        System.out.println("Remove first: " + deque.removeFirst());
        System.out.println("Remove last: " + deque.removeLast());
        System.out.println("After removals: " + deque);

        // ArrayDeque - more efficient than LinkedList for Deque operations
        Deque<Integer> arrayDeque = new ArrayDeque<>();
        arrayDeque.addFirst(1);
        arrayDeque.addLast(2);
        arrayDeque.addFirst(0);
        arrayDeque.addLast(3);

        System.out.println("\nArrayDeque: " + arrayDeque);

        // Stack operations using Deque
        Deque<String> stack = new ArrayDeque<>();
        stack.push("First");
        stack.push("Second");
        stack.push("Third");

        System.out.println("\nStack: " + stack);
        System.out.println("Pop: " + stack.pop());
        System.out.println("Peek: " + stack.peek());

        // Queue simulation with Deque
        Deque<String> queue = new ArrayDeque<>();
        queue.offer("Task1");
        queue.offer("Task2");
        queue.offer("Task3");

        System.out.println("\nQueue: " + queue);
        System.out.println("Process: " + queue.poll());
        System.out.println("Next: " + queue.peek());

        // PriorityQueue with custom objects
        Queue<Task> taskQueue = new PriorityQueue<>((t1, t2) -> {
            if (t1.getPriority() != t2.getPriority()) {
                return Integer.compare(t2.getPriority(), t1.getPriority()); // Higher priority first
            }
            return t1.getName().compareTo(t2.getName());
        });

        taskQueue.offer(new Task("Low Priority Task", 1));
        taskQueue.offer(new Task("High Priority Task", 5));
        taskQueue.offer(new Task("Medium Priority Task", 3));
        taskQueue.offer(new Task("Another High Priority", 5));

        System.out.println("\nTask Queue:");
        while (!taskQueue.isEmpty()) {
            Task task = taskQueue.poll();
            System.out.println("Processing: " + task.getName() + " (Priority: " + task.getPriority() + ")");
        }
    }
}

// Task class for PriorityQueue demonstration
class Task {
    private String name;
    private int priority;

    public Task(String name, int priority) {
        this.name = name;
        this.priority = priority;
    }

    public String getName() { return name; }
    public int getPriority() { return priority; }

    @Override
    public String toString() {
        return "Task{name='" + name + "', priority=" + priority + '}';
    }
}
